import React, { useState } from 'react';
import './MultiStepForm.css'; // Import your CSS file
import { Form, InputGroup, Row, Button, ProgressBar } from 'react-bootstrap';

const MultiStepForm = () => {
  const [step, setStep] = useState(1);

  const handleNext = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const handlePrevious = () => {
    setStep((prevStep) => prevStep - 1);
  };
  const [form, setForm] = useState({
    first_name: '',
    last_name: '',
    mobile: '',
    email: '',
    address1: '',
    address2: '',
    city: '',
    a_state: '',
    pin: '',
    menu: '',
    order: '',
    images: [],
  });
  const handleChange = (e) => {
    if (e.target.type === 'file') {
      const selectedFiles = Array.from(e.target.files);
      setForm({ ...form, images: selectedFiles });
    } else {
      setForm({ ...form, [e.target.name]: e.target.value });
    }
  };
  
  return (
    <div id="msform">
      <ul id="progressbar">
        <li className={step === 1 ? 'active' : ''}> الخطوة الاولى</li>
        <li className={step === 2 ? 'active' : ''}>الخطوة الثانية</li>
        <li className={step === 3 ? 'active' : ''}>الخطوة الثالثة</li>
        <li className={step === 4 ? 'active' : ''}>الخطوة الرابعة</li>
      </ul>

      {step === 1 && (
        <fieldset style={{ border: '1px solid #ccc', padding: '20px', marginBottom: '20px' }}>
          <h2 className="fs-title">إرفاق صور</h2>
          <h3 className="fs-subtitle">الخطوة الاولى</h3>
          <Form.Group controlId="formGridFile" className="col col-sm-6">
            <Form.Label>إرفاق صورة</Form.Label>
            <Form.Control type="file" name="images" onChange={handleChange} multiple />
          </Form.Group>
          <input type="button" name="next" className="next action-button" value="Next" onClick={handleNext} />
        </fieldset>
      )}

      {step === 2 && (
        <fieldset style={{ border: '1px solid #ccc', padding: '20px', marginBottom: '20px' }}>
          <h2 className="fs-title">Social Profiles</h2>
          <h3 className="fs-subtitle">Your presence on the social network</h3>
          {/* ... other form fields for step 2 */}
          <input type="button" name="previous" className="previous action-button" value="Previous" onClick={handlePrevious} />
          <input type="button" name="next" className="next action-button" value="Next" onClick={handleNext} />
        </fieldset>
      )}

      {step === 3 && (
        <fieldset style={{ border: '1px solid #ccc', padding: '20px', marginBottom: '20px' }}>
          <h2 className="fs-title">Personal Details</h2>
          <h3 className="fs-subtitle">We will never sell it</h3>
          {/* ... other form fields for step 3 */}
          <input type="button" name="previous" className="previous action-button" value="Previous" onClick={handlePrevious} />
          <input type="button" name="next" className="next action-button" value="Next" onClick={handleNext} />
        </fieldset>
      )}

      {step === 4 && (
        <fieldset style={{ border: '1px solid #ccc', padding: '20px', marginBottom: '20px' }}>
          <h2 className="fs-title">Personal Details</h2>
          <h3 className="fs-subtitle">We will never sell it</h3>
          {/* ... other form fields for step 4 */}
          <input type="button" name="previous" className="previous action-button" value="Previous" onClick={handlePrevious} />
          <a href="https://twitter.com/GoktepeAtakan" className="submit action-button" target="_top">Submit</a>
        </fieldset>
      )}
    </div>
  );
};

export default MultiStepForm;
