import React, { useState, useEffect } from 'react';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    state: '',
    zip: '',
    website: '',
    hosting: 'yes',
    comment: '',
  });

  const [successMessageVisible, setSuccessMessageVisible] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  useEffect(() => {
    if (successMessageVisible) {
      setTimeout(() => {
        setSuccessMessageVisible(false);
      }, 5000);
    }
  }, [successMessageVisible]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // You can add your form submission logic here
    // For simplicity, just show the success message for now
    setSuccessMessageVisible(true);

    // Clear the form data after submission
    setFormData({
      first_name: '',
      last_name: '',
      email: '',
      phone: '',
      address: '',
      city: '',
      state: '',
      zip: '',
      website: '',
      hosting: 'yes',
      comment: '',
    });
  };

  return (
    <div className="container">
      <form className="well form-horizontal" onSubmit={handleSubmit}>
        <fieldset>
          <legend>Contact Us Today!</legend>

          {/* Your form inputs here */}

          {/* Success message */}
          {successMessageVisible && (
            <div className="alert alert-success" role="alert">
              Success <i className="glyphicon glyphicon-thumbs-up"></i> Thanks for contacting us, we will get back to you shortly.
            </div>
          )}

          {/* Button */}
          <div className="form-group">
            <label className="col-md-4 control-label"></label>
            <div className="col-md-4">
              <button type="submit" className="btn btn-warning">
                Send <span className="glyphicon glyphicon-send"></span>
              </button>
            </div>
          </div>
        </fieldset>
      </form>
    </div>
  );
};

export default ContactForm;
