// MultiStepForm.js
import React, { useState } from 'react';
import './MultiStepForm.css';
import { Form, InputGroup, Row, Button, ProgressBar, FormControl, FormLabel } from 'react-bootstrap';
import FormCheckLabel from 'react-bootstrap/esm/FormCheckLabel';
import { Checkbox, FormControlLabel, RadioGroup } from '@mui/material';

import Slider from '@mui/material/Slider';
import Radio from '@mui/material/Radio';


const MultiStepForm = () => {
  const [step, setStep] = useState(1);
  const marks = [
    {
      value: 0,
      label: '1م',
    },
    {
      value: 20,
      label: '3م',
    },
    {
      value: 37,
      label: '5م',
    },
    {
      value: 100,
      label: '6م',
    },
  ];
  const handleNext = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const handlePrevious = () => {
    setStep((prevStep) => prevStep - 1);
  };
  const [form, setForm] = useState({
    first_name: '',
    last_name: '',
    mobile: '',
    email: '',
    address1: '',
    address2: '',
    city: '',
    a_state: '',
    pin: '',
    menu: '',
    order: '',
    images: [],
  });
  const handleChange = (e) => {
    if (e.target.type === 'file') {
      const selectedFiles = Array.from(e.target.files);
      setForm({ ...form, images: selectedFiles });
    } else {
      setForm({ ...form, [e.target.name]: e.target.value });
    }
  };
  function valuetext(value: number | number[]) {
    return `${value}م`;
  }
  
  return (
    <div id="msform">
      <ul id="progressbar">
        <li className={step === 1 ? 'active' : ''}> الخطوة الاولى</li>
        <li className={step === 2 ? 'active' : ''}>الخطوة الثانية</li>
        <li className={step === 3 ? 'active' : ''}>الخطوة الثالثة</li>
        <li className={step === 4 ? 'active' : ''}>الخطوة الثالثة</li>
      </ul>

      {step === 1 && (
        <fieldset>
            
          <h2 className="fs-title">إرفاق صور</h2>
          <h3 className="fs-subtitle">الخطوة الاولى</h3>
          {/* <input type="text" name="email" placeholder="Email" />
          <input type="password" name="pass" placeholder="Password" />
          <input type="password" name="cpass" placeholder="Confirm Password" />
         */}
           <Form.Group controlId="formGridFile" className="col col-sm-6">
                <Form.Label>إرفاق صورة</Form.Label>
                <Form.Control type="file" name="images" onChange={handleChange} multiple />
              </Form.Group>
              <input type="button" name="next" className="next action-button" value="Next" onClick={handleNext} /> 
        </fieldset>
      )}

      {step === 2 && (
        <fieldset>

          <h2 className="fs-title">الخطوة الثانية</h2>
          <h3 className="fs-subtitle">تفاصيل القطعة</h3>
          <input type="number" name="twitter" placeholder="Twitter" />
          <input type="text" name="facebook" placeholder="رقم المخطط" />
          <input type="text" name="gplus" placeholder="رقم القطعة" />
          <FormControlLabel
    style={{ textAlign: 'left', marginLeft: '0', width: '100%' }}
    required
    control={<Checkbox />}
    label="اظهار معلومات المخطط ضمن الاعلان"
  />
          <input type="button" name="previous" className="previous action-button" value="Previous" onClick={handlePrevious} />
          <input type="button" name="next" className="next action-button" value="Next" onClick={handleNext} />
        </fieldset>
      )}

      {step === 3 && (
        <fieldset>
          <h2 className="fs-title">الحطوة الثالثة</h2>
          <h3 className="fs-subtitle">ادخل تفاصيل الاعلان</h3>
          <input type="text" name="fname" placeholder="السعر الاجمالي" />
          <textarea  type="text" name="lname" placeholder="وصف العقار" />
        
          <input type="button" name="previous" className="previous action-button" value="Previous" onClick={handlePrevious} />
          <input type="button" name="next" className="next action-button" value="Next" onClick={handleNext} />
        </fieldset>
      )}
      {step === 4 && (
        <fieldset>
          <h2 className="fs-title">الخطوة الرابعة</h2>
          <h3 className="fs-subtitle">ادخل تفاصيل الاعلان</h3>
          <input type="text" name="fname" placeholder="عرض الشارع" />

  <div className="form-group">
    <label className="slider-label" > عرض الشارع</label>
    <Slider
      aria-label="Custom marks"
      defaultValue={20}
      getAriaValueText={valuetext}
      step={10}
      valueLabelDisplay="auto"
      marks={marks}
    />
  </div>
  

      <FormLabel id="demo-row-radio-buttons-group-label">Gender</FormLabel>
      <RadioGroup
        row
        aria-labelledby="demo-row-radio-buttons-group-label"
        name="row-radio-buttons-group"
      >
        <FormControlLabel value="female" control={<Radio />} label="عزاب" />
        <FormControlLabel value="male" control={<Radio />} label="عوائل" />
        
      </RadioGroup>
 
          <input type="text" name="lname" placeholder="Last Name" />
          <input type="text" name="phone" placeholder="Phone" />
          
          <textarea name="address" placeholder="Address"></textarea>
          <input type="button" name="previous" className="previous action-button" value="Previous" onClick={handlePrevious} />
          <a href="https://twitter.com/GoktepeAtakan" className="submit action-button" target="_top">Submit</a>
        </fieldset>
      )}
    </div>
  );
};

export default MultiStepForm;
