import React, { useState } from 'react';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    state: '',
    zip: '',
    website: '',
    hosting: 'yes',
    comment: '',
  });

  const [successMessage, setSuccessMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add your form submission logic here
    // You can use formData to send the form data to your server or perform any other actions
    setSuccessMessage('Thanks for contacting us, we will get back to you shortly.');
  };

  return (
    <div className="container">
      <form className="well form-horizontal" onSubmit={handleSubmit}>
        <fieldset>
          <legend>Contact Us Today!</legend>

          {/* ... (Other form elements) */}
          {/* Success message */}
          {successMessage && (
            <div className="alert alert-success" role="alert">
              {successMessage} <i className="glyphicon glyphicon-thumbs-up"></i>
            </div>
          )}

          {/* Button */}
          <div className="form-group">
            <label className="col-md-4 control-label"></label>
            <div className="col-md-4">
              <button type="submit" className="btn btn-warning">
                Send <span className="glyphicon glyphicon-send"></span>
              </button>
            </div>
          </div>
        </fieldset>
      </form>
    </div>
  );
};

export default ContactForm;
