import React, { useEffect, useState } from "react"
import style from "./style.css"
import MyTable from "./MyTable ";
import RatingForm from "./RatingForm";
import { useParams } from "react-router-dom/cjs/react-router-dom.min";


const Product = (   ) => {
//     const { id } = match.params;
//   const [ad, setAd] = useState(null);
  let { id } = useParams();

  const [ad, setAd] = useState(null);

  useEffect(() => {
    async function fetchAdDetails() {
      try {
        const response = await fetch(`http://localhost:9000/users/allAdvertisement/${id}`);
        const data = await response.json();
  
        if (response.ok) {
          setAd(data);
        } else if (response.status === 404) {
          console.warn("Ad not found:", response.statusText);
          // Handle the case where the ad is not found
        } else {
          console.error("Failed to fetch ad details. Status:", response.status);
        }
      } catch (error) {
        console.error("Error fetching ad details:", error);
      }
    }
  
    fetchAdDetails();
  }, [id]);
  
  
  
  if (!ad) {
    console.log("Failed to fetch ad details. Status:", ad.description);

    return <div>Loading...</div>;
  }
  console.log("Failed to fetch ad details. Status:", ad.description);
    
    return (
        <>
            <section className="h-100 gradient-custom">
                <div className="container py-5 h-100">
                    <div className="row d-flex justify-content-center align-items-center h-100">
                        <div className="col col-lg-9 col-xl-7">
                            <div className="card custam-card">
                            {/* {Advertisement.map((ad, index) => (
  <div key={index}> */}


                                <div className="ms-3" style={{ marginTop: '30px' }} >
                                    <div className="p-4 text-black" style={{ backgroundColor: '#f8f9fa' }}>
                                        <div className="d-flex justify-content-start text-center py-1">
                                            <div>
                                            <h5>{ad.description}</h5>

                                            </div>
                                        </div>
                                    </div>
                                </div>
                                <div className="row g-3 mt-1 mx-1">
                                    <div className="col mb-2">
                                        <img
                                            src="https://mdbcdn.b-cdn.net/img/Photos/Lightbox/Original/img%20(112).webp"
                                            alt="image 1"
                                            className="w-100 rounded-3"
                                        />
                                    </div>
                                    <div className="col mb-2">
                                        <img
                                            src="https://mdbcdn.b-cdn.net/img/Photos/Lightbox/Original/img%20(107).webp"
                                            alt="image 1"
                                            className="w-100 rounded-3"

                                        />
                                    </div>
                                </div>
                                <div className="row g-2 mx-1">
                                    <div className="col">
                                        <img
                                            src="https://mdbcdn.b-cdn.net/img/Photos/Lightbox/Original/img%20(108).webp"
                                            alt="image 1"
                                            className="w-100 rounded-3"
                                        />
                                    </div>
                                    <div className="col">
                                        <img
                                            src="https://mdbcdn.b-cdn.net/img/Photos/Lightbox/Original/img%20(114).webp"
                                            alt="image 1"
                                            className="w-100 rounded-3"
                                        />
                                    </div>
                                </div>
                                <div className="card-body p-4 text-black">
                                    <p className="lead fw-normal mb-1">معلومات المعلن</p>

                                    <div className="mb-5">
                                        <div className="p-4" style={{ backgroundColor: '#f8f9fa' }}>
                                            <p className="font-italic mb-1">نقدم لكم افضل العروض العقاريه باقل الاسعار
                                                ادوار ارضيه وعلويه شغل مودرن
                                                مساحه 330 متر الدور الارضي عليه 550 الف
                                                الدور العلوي عليه 570 الف قابل للتفاوض
                                                ضمانات علي الكهرب والسباكه 25 سنه
                                                ضمانات علي العوازل والخزانات وبايكه 15 سنه عوازل حراريه ومائيه
                                                اشراف هندسي كامل وبناء علي الكود السعودي الجديد وتأمين بلديه
                                                نتعامل مع جميع البنوك لتوفير الحلول التمويليه الميسره
                                                سداد مديونيات وتوفير الدفعه المقدمه بدون فوايد
                                                جميع العروض والاسعار المناسبه لجميع الشرائح
                                                0535736454.  0549620387</p>
                                            <p className="font-italic mb-1">Lives in New York</p>
                                            <p className="font-italic mb-0">Photographer</p>
                                        </div>
                                    </div>
                                    {/* <div className="d-flex justify-content-between align-items-center mb-4">
                                        <p className="lead fw-normal mb-0">Recent photos</p>
                                        <p className="mb-0">
                                            <a href="#!" className="text-muted">
                                                Show all
                                            </a>
                                        </p>
                                    </div> */}
                                    <p className="lead fw-normal mb-1">معلومات المعلن</p>
                                    <div className="p-4 text-black" style={{ backgroundColor: '#f8f9fa', display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center' }}>
                                        <div className="d-flex justify-content-start text-center py-1">
                                            <div>
                                                <p className="mb-1 h5">شركة أعمار الملقا للتطوير والاستثمار العقاري</p>
                                                <p className="small text-muted mb-0"></p>
                                            </div>
                                        </div>
                                        <RatingForm/>
                                        

                                        <div className="btn-flex">


                                            <button type="button" className="btn btn-outline-dark mt-5 " data-mdb-ripple-color="" style={{ zIndex: 1 }}>
                                                إظهار رقم الجوال    </button>
                                            <button type="button" className="btn btn-outline-dark mt-5 m" data-mdb-ripple-color="" style={{ zIndex: 1, marginRight: 6 }}>
                                                المراسلة   </button>
                                        </div>
                                    </div>

                                    <br />
                                    <div className="p-4 text-black" style={{ backgroundColor: '#f8f9fa' }}>
                                        <div className="d-flex justify-content-start text-center py-1">
                                            <MyTable />

                                        </div>
  
                                    </div>
                                    
                                </div>
                                {/* </div>
                                   ))} */}
                                
                            </div>
                            
                        </div>
                    </div>
                </div>

            </section>
        </>
    );
};

export default Product
