import React, { useContext, useState } from "react";
import { useHistory } from "react-router-dom";  // Import useHistory from react-router-dom
import {
  BoldLink,
  BoxContainer,
  FormContainer,
  Input,
  LineText,
  MutedLink,
  SubmitButton,
} from "./comman";
import { Marginer } from "../marginer";
import { AccountContext } from './AccountContext';

export function LoginForm(props) {
  const { switchToSignup } = useContext(AccountContext);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');

  const history = useHistory();  // Initialize useHistory

  // async function loginUser(event) {
  //   event.preventDefault();
  //   const response = await fetch('http://localhost:9000/users/login', {
  //     method: 'POST',
  //     headers: {
  //       'Content-Type': 'application/json',
  //     },
  //     body: JSON.stringify({
  //       email,
  //       password,
  //     }),
  //   });

  //   const data = await response.json();
  //   console.log(data);

  //   if (data.user) {
  //     localStorage.setItem('token', data.user);
  //     alert("Login successful");
  //     window.location.href='/'

      
  //   } else {
  //     alert("Please check your username or password");
  //   }
  // }
  async function loginUser(event) {
    event.preventDefault();
    const response = await fetch('http://localhost:9000/users/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        email,
        password,
        phoneNumber, // Include phone number in the request body
      }),
    });

    const data = await response.json();
    console.log(data);

    if (data.user) {
      localStorage.setItem('token', data.user);

      // Check if phone number is verified
      if (!data.user.isPhoneVerified) {
        alert("Phone number is not verified. Redirecting to verification page...");
        // Redirect to the phone number verification page
        history.push('/phone-verification');
      } else {
        alert("Login successful");
        window.location.href='/';
      }
    } else {
      alert("Please check your username, password, or phone number");
    }
  }
  return (
    <BoxContainer>
      <FormContainer onSubmit={loginUser}>
        <Input
          type="email"
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <Input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <Input
  type="text"
  placeholder="Phone Number"
  value={phoneNumber}
  onChange={(e) => setPhoneNumber(e.target.value)}
/>

        <Marginer direction="vertical" margin={10} />
        <MutedLink href="#">Forget your password?</MutedLink>
        <Marginer direction="vertical" margin="1.6em" />
        <SubmitButton type="submit">Signin</SubmitButton>
      </FormContainer>
      <Marginer direction="vertical" margin="5px" />
      <LineText>
        Don't have an account?{" "}
        <BoldLink onClick={switchToSignup} href="#">
          Signup
        </BoldLink>
      </LineText>
    </BoxContainer>
  );
}
