import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ContactForm = () => {
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    state: '',
    zip: '',
    website: '',
    hosting: 'yes',
    comment: '',
  });

  const [successMessageVisible, setSuccessMessageVisible] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  useEffect(() => {
    if (successMessageVisible) {
      setTimeout(() => {
        setSuccessMessageVisible(false);
      }, 5000);
    }
  }, [successMessageVisible]);

  const handleSubmit = (e) => {
    e.preventDefault();

    // You can add your form submission logic here
    // For simplicity, just show the success message for now
    setSuccessMessageVisible(true);

    // Clear the form data after submission
    setFormData({
      first_name: '',
      last_name: '',
      email: '',
      phone: '',
      address: '',
      city: '',
      state: '',
      zip: '',
      website: '',
      hosting: 'yes',
      comment: '',
    });
  };

  return (
    <div className="container">
      <form className="well form-horizontal" onSubmit={handleSubmit}>
        <fieldset>
          <legend>Contact Us Today!</legend>

           {/* First Name */}
           <div className="form-group">
            <label className="col-md-4 control-label">First Name</label>
            <div className="col-md-4 inputGroupContainer">
              <div className="input-group">
                <span className="input-group-addon"><i className="glyphicon glyphicon-user"></i></span>
                <input
                  name="first_name"
                  placeholder="First Name"
                  className="form-control"
                  type="text"
                  value={formData.first_name}
                  onChange={handleInputChange}
                />
              </div>
            </div>
          </div>
          <div className="form-group">
  <label className="col-md-4 control-label">E-Mail</label>  
  <div className="col-md-4 inputGroupContainer">
    <div className="input-group">
      <span className="input-group-addon"><i className="glyphicon glyphicon-envelope"></i></span>
      <input name="email" placeholder="E-Mail Address" className="form-control" type="text"/>
    </div>
  </div>
</div>

          {/* Last Name */}
          <div className="form-group">
            <label className="col-md-4 control-label">Last Name</label>
            <div className="col-md-4 inputGroupContainer">
              <div className="input-group">
                <span className="input-group-addon"><i className="glyphicon glyphicon-user"></i></span>
                <input
                  name="last_name"
                  placeholder="Last Name"
                  className="form-control"
                  type="text"
                  value={formData.last_name}
                  onChange={handleInputChange}
                />
              </div>
            </div>
          </div>
{/* 
          <!-- Text input--> */}
       <div class="form-group">
  <label class="col-md-4 control-label">E-Mail</label>  
    <div class="col-md-4 inputGroupContainer">
    <div class="input-group">
        <span class="input-group-addon"><i class="glyphicon glyphicon-envelope"></i></span>
  <input name="email" placeholder="E-Mail Address" class="form-control"  type="text"/>
    </div>
  </div>
</div>

{/* 
<!-- Text input--> */}

          {/* Success message */}
          {successMessageVisible && (
            <div className="alert alert-success" role="alert">
              Success <i className="glyphicon glyphicon-thumbs-up"></i> Thanks for contacting us, we will get back to you shortly.
            </div>
          )}

          {/* Button */}
          <div className="form-group">
            <label className="col-md-4 control-label"></label>
            <div className="col-md-4">
              <button type="submit" className="btn btn-warning">
                Send <span className="glyphicon glyphicon-send"></span>
              </button>
            </div>
          </div>
        </fieldset>
      </form>
    </div>
  );
};

export default ContactForm;
