import React from "react"
import { Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import ApartmentsForRent from "./ApartmentsForRent"; // Import the component for "شقق للإيجار"
const Categories = () => {
  const data = [
    {
      cateImg: "./images/category/cat1.png",
      cateName: "شقق للإيجار",
    },
    {
      cateImg: "./images/category/cat2.png",
      cateName: "أراضي للبيع",
    },
    {
      cateImg: "./images/category/cat3.png",
      cateName: "سيارات",
    },
    {
      cateImg: "./images/category/cat4.png",
      cateName: " شقق للبيع",
    },
    {
      cateImg: "./images/category/cat5.png",
      cateName: "عمائر للبيع",
    },
    {
      cateImg: "./images/category/cat6.png",
      cateName: "محلات للإيجار",
    },
    {
      cateImg: "./images/category/cat7.png",
      cateName: "استراحة للبيع",
    },
    {
      cateImg: "./images/category/cat8.png",
      cateName: " مزرعة للبيع",
    },
    {
      cateImg: "./images/category/cat9.png",
      cateName: " استراحة للإيجار",
    },
    {
      cateImg: "./images/category/cat10.png",
      cateName: "مستودع للإيجار",
    },
    {
      cateImg: "./images/category/cat11.png",
      cateName: "شقة مفروشة",
    },
  ]
  const componentMap = {
    "شقق للإيجار": <ApartmentsForRent />,
    // "أراضي للبيع": <LandsForSale />,
    // Add other components
  };
  return (
    <>
    <div className='category'>
      {data.map((value, index) => (
        <Link to={`/category/${value.cateName}`} key={index} className='box f_flex'>
          <img src={value.cateImg} alt='' />
          <span>{value.cateName}</span>
        </Link>
      ))}
    </div>
    {/* Render the dynamically selected component */}
    {componentMap["شقق للإيجار"]} {/* This will render the component for "شقق للإيجار" */}
  </>
  )
}

export default Categories
